import React from 'react';
//mport Fitbit from './fitbit';
import classes from'./App.module.css';

function App() {

  const  logoPink_src="png/fit_band_pink.png";
  const  logoBlack_src="png/fit_band_black.png";
  const  logoBrown_src="png/fit_band_brown.png";
  const  logoOrange_src="png/fit_band_orange.png";
  const  Heart_src    =  "png/heart.svg";  

  const timeBtn =document.getElementByclass('time-btn');
  const heartBtn=document.getElementByclass('heart-btn');      




        function time(){
          const  now      = new Date,
            hours   = now.getHours(),
            ampm    = hours<12 ? ' AM' : ' PM',
            minutes = now.getMinutes(),
            //seconds = now.getSeconds(),
            time_str   = (hours>12) ? [ (hours-12), //otherwise: what's the use of AM/PM?
                  (minutes < 10 ? "0" + minutes : minutes),
                  // (seconds < 10 ? "0" + seconds : seconds) 
                  ]
                   .join(":") + ampm
          //else part of if condition;
          : [(hours),										 //otherwise: what's the use of AM/PM?
          (minutes < 10 ? "0" + minutes : minutes),
          //(seconds < 10 ? "0" + seconds : seconds)
          ]
          .join(":") + ampm ;

          setTimeout(time,1000);
          return time_str;
          }

  return (  

    <div className={classes.AppHeader} >
      <header className={classes.mainHeader}>
          <nav className={classes.Topbar}>

                <img src={logoPink_src}  className={classes.logo } alt='logoImage' />
                <span className={classes.logo_text}>GearOS</span>
                <button type="button" className={classes.loginBtn}>Login</button>

          </nav>
      </header>
      <div className={classes.mainDiv}>
          <section className={classes.ImageSection}>
          <img src={logoPink_src}  className={classes.MainProductImage} alt='logoImage' />

            <div className={classes.FeaturesItem} > 

            <p> {` ${time()}`}</p>
            </div>

             <div className={classes.FeaturesItem2} > 
             <img src={Heart_src}  className={[classes.logo,classes.heart] .join('') } alt='logoImage' />
            <p> 50</p>
            </div>

          <button  className={classes.buybtn2} > Buy Now        </button> 
          </section>
          <section className={classes.contentSection}>
          <h3 className={classes.headerText} >Gear OS 2-Works on Android and iPhone</h3>
          <p className={classes.ContentText} >Wear OS by Gogle works with phones running Android 6.0+ (excluding Go edition) or iOS 10.0+</p>
           <p className={classes.Text} >Select color</p>




          <div className={classes.c}>

                <img src={logoPink_src}  className={[classes.productImg,classes.SelectedProductImg].join(' ')} alt='logoImage' />
                <img src={logoBlack_src}  className={classes.productImg} alt='logoImage' />
                <img src={logoBrown_src}  className={classes.productImg} alt='logoImage' />
                <img src={logoOrange_src}  className={classes.productImg} alt='logoImage' />


              </div>

        <div>
          <p className={classes.Text} >Features</p>

          <button className={[classes.btn,classes.timebtn].join('')}  > Time          </button>
          <button className={[classes.btn,classes.heartbtn].join('')}  > Heart rate    </button>
          {/* <button className={classes.btn}  > Android       </button>
					<button className={classes.btn}  > Geolaction    </button> */}




          </div>
          {/* <button  className={classes.buybtn} > Buy Now        </button> */}
          </section>

      </div>
    </div> 

  );
}

export default App;
